    private void panelRound5MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_panelRound5MouseClicked
         // Obtener los valores de los campos de texto
    String nombre = txtNombre.getText();
    String apellidos = txtApellidos.getText();
    String correo = txtCorreo.getText();
    String contraseña = txtContraeña.getText();
    String departamento = txtDepartamento.getText();
    String numeroDocumento = txtNumeroDocumento.getText();
    String tipoDocumento = cbxTipoDocumento.getSelectedItem().toString();
    int edad;

    // Validar y convertir la edad
    try {
        edad = Integer.parseInt(txtEdad.getText());
    } catch (NumberFormatException e) {
        JOptionPane.showMessageDialog(this, "La edad debe ser un número válido.", "Error", JOptionPane.ERROR_MESSAGE);
        return;
    }

    // Validar que no haya campos vacíos
    if (nombre.isEmpty() || apellidos.isEmpty() || correo.isEmpty() || contraseña.isEmpty() || 
        departamento.isEmpty() || numeroDocumento.isEmpty() || tipoDocumento.isEmpty()) {
        JOptionPane.showMessageDialog(this, "Todos los campos son obligatorios.", "Error", JOptionPane.ERROR_MESSAGE);
        return;
    }

    // Insertar en la base de datos
    String query = "INSERT INTO cliente (clinombre, cliapellido, clicorreo, clicontraseña, tipo_documento, numero_documento, departamento, edad, es_socio) "
                 + "VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?)";
    try (Connection connection = DriverManager.getConnection("jdbc:mysql://localhost/bd_peliculas", "root", "Katiaalfa*2024");
         PreparedStatement stmt = connection.prepareStatement(query)) {
        
        // Configurar los parámetros del PreparedStatement
        stmt.setString(1, nombre);
        stmt.setString(2, apellidos);
        stmt.setString(3, correo);
        stmt.setString(4, contraseña);
        stmt.setString(5, tipoDocumento);
        stmt.setString(6, numeroDocumento);
        stmt.setString(7, departamento);
        stmt.setInt(8, edad);
        stmt.setBoolean(9, true); // Es socio (TRUE)

        // Ejecutar la consulta
        int filasAfectadas = stmt.executeUpdate();
        if (filasAfectadas > 0) {
            JOptionPane.showMessageDialog(this, "Cliente registrado exitosamente.", "Éxito", JOptionPane.INFORMATION_MESSAGE);
            limpiarCampos(); // Método opcional para limpiar los campos
        } else {
            JOptionPane.showMessageDialog(this, "No se pudo registrar al cliente.", "Error", JOptionPane.ERROR_MESSAGE);
        }
    } catch (SQLException e) {
        JOptionPane.showMessageDialog(this, "Error al conectar con la base de datos: " + e.getMessage(), "Error", JOptionPane.ERROR_MESSAGE);
    }
    }//GEN-LAST:event_panelRound5MouseClicked

