    private void limpiarCampos() {
    txtNombre.setText("");
    txtApellidos.setText("");
    txtCorreo.setText("");
    txtContraeña.setText("");
    txtDepartamento.setText("");
    txtNumeroDocumento.setText("");
    txtEdad.setText("");
    cbxTipoDocumento.setSelectedIndex(0); 
}

